package classpractis;

public class char93 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=0;i<5;i++)
		{
			
			for(int j=0;j<9;j++)
			{
               if(j-i==0)
              {
           	   System.out.print("N");
              }
               else if(j+i==8)
               {
            	   System.out.print("N");
               }
               else
               {
            	   System.out.print(" ");
               }
	         }
			System.out.println();

	}

}}
