package classpractis;

public class star86 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=0;i<5;i++)
		{
			
			for(int j=0;j<5;j++)
			{
               if(j-i==0)
              {
           	   System.out.print("0");
              }
             
               
               else
               {
            	   System.out.print("X");
               }
	         }
			System.out.println();

	}

	}

}
