package day133011;

public class variableTypeDemo {
	
static int number1;//instance
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		variableTypeDemo ob= new variableTypeDemo();
		
		System.out.println("method1:"+ ob.number1);
		

		variableTypeDemo ob1= new variableTypeDemo();
		ob1.number1=100;
		System.out.println("method1:"+ ob1.number1);
		
	
		variableTypeDemo ob2= new variableTypeDemo();
	
	System.out.println("method1:"+ ob2.number1);
	

	

	}

}
